<script setup lang="ts">
import { usePopup } from '~/composable/usePopup';
import MobileSidebar from './MobileSidebar.vue';
import { useLinks } from '~/composable/useSocialLinks';

    const { popupState, setOpen } = usePopup()
    const isMobileMenuOpen = ref(false)
    const isScrolled = ref(false)

    const toggleMobileMenu = () => {
        isMobileMenuOpen.value = !isMobileMenuOpen.value
        
        // Управление overflow body
        if (isMobileMenuOpen.value) {
            document.body.style.overflow = 'hidden'
        } else {
            document.body.style.overflow = ''
        }
    }

    const handleScroll = () => {
        const heroBlock = document.querySelector('.hero-block')
        if (heroBlock) {
            const heroBlockRect = heroBlock.getBoundingClientRect()
            // Когда верх hero-block достигает верха viewport
            isScrolled.value = heroBlockRect.top <= 0
        }
    }

    onMounted(() => {
        window.addEventListener('scroll', handleScroll)
        handleScroll() // Проверяем начальное состояние
    })

    onUnmounted(() => {
        window.removeEventListener('scroll', handleScroll)
    })

    const { vkGroup, vkMuseu } = useLinks
</script>

